import './App.css';
import { useState,useEffect } from 'react';
import axios from 'axios'
function App() {
  const [location,setLocation]=useState('')
  const [weatherData,setWeatherData]=useState(null)

  const API_KEY = "0306f3e1767baa02731f4d5bb9b8cff4";
  const API_URL = 'https://api.openweathermap.org/data/2.5/weather';

  const fetchWeatherData=async()=>{
    try{

      const response=await axios.get(API_URL,{params:{q:location,appid:API_KEY,units:"metric"}})
      console.log(response.data)
      setWeatherData(response.data)


    }catch(err){
      console.error("Error to Fetching Weather data",err)
    }

  }

  useEffect(()=>{
    if(location){
      fetchWeatherData()
    }
  },[location])


 const handlecheck=(e)=>{
  setLocation(e.target.value)
 }
  return (
    <div className="App">

       <input type='text' value={location} onChange={handlecheck}/>

       {weatherData && (
        <div>
          <h2>{weatherData.name}</h2>
          <h2>{}</h2>
        </div>
       )}





    </div>
  );
}

export default App;
