import React,{useEffect} from 'react';

import moment from 'moment'; 
import {Container, Card,Row,Col} from 'react-bootstrap';

const Body = ({weatherData,setWeatherData,location}) => {
    // const API_KEY = "0306f3e1767baa02731f4d5bb9b8cff4";
    // const API_URL = 'https://api.openweathermap.org/data/2.5/weather';

    // const fetchWeatherData=async()=>{
    //   try{

    //     const response=await axios.get(API_URL,{params:{q:location,appid:API_KEY,units:"metric"}})
    //     console.log(response.data)
    //     setWeatherData(response.data)


    //   }catch(err){
    //     console.error("Error to Fetching Weather data",err)
    //   }

    // }

    // useEffect(()=>{
    //   if(location){
    //     fetchWeatherData()
    //   }
    // },[location])
  return (
    <div>
        {weatherData && (
         <Container className="mt-5" >
        <Card style={{backgroundColor:"#022D36",color:"white",width:900,height:300}}>
          <Card.Body>
            <Card.Title>{weatherData.name}</Card.Title>
            <Card.Subtitle >{moment().format('dddd')}, {moment().format('LL')}</Card.Subtitle>
            <Row>
              <Col md={6}>

                <ul style={{ listStyleType: 'none', padding: 0,paddingTop:10 }}>
                  <li>Temprature: {weatherData.main.temp} &deg;C</li>
                  <li>Humidity: {weatherData.main.humidity} %</li>

                </ul>
              </Col>
              <Col md={6}>

                <ul style={{ listStyleType: 'none', padding:0,position:'absolute' }}>
                  <li>Sunrise: {new Date(weatherData.sys.sunrise * 1000).toLocaleTimeString('en-IN')}</li>
                  <li>Sunset: {new Date(weatherData.sys.sunset * 1000).toLocaleTimeString('en-IN')}</li>

                </ul>
              </Col>
            </Row>

          </Card.Body>
        </Card>
      </Container>
        )}


    </div>
  );
}

export default Body;
