import React from "react";
import styled from "styled-components";

const Container = styled.div`
  background-color: black;
  height: 10vh;
  width: 100vw;
  color: gray;
`;
const Nav = styled.div`
  padding: 10px;
`;
const NavItems = styled.div`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: space-around;
`;
const Title = styled.h1``;
const SearchContainer = styled.div`
  width: 30px;
  height: 100px;`;


const SearchButton = styled.div`
position: absolute;
margin: auto;
top: 0;
right: 0;
bottom: 0;
left: 0;
width: 80px;
height: 80px;
background: crimson;
border-radius: 50%;
transition: all 1s;
z-index: 4;
box-shadow: 0 0 25px 0 rgba(0, 0, 0, 0.4);

&:hover {
  cursor: pointer;
}

&::before {
  content: "";
  position: absolute;
  margin: auto;
  top: 22px;
  right: 0;
  bottom: 0;
  left: 22px;
  width: 12px;
  height: 2px;
  background: white;
  transform: rotate(45deg);
  transition: all 0.5s;
}

&::after {
  content: "";
  position: absolute;
  margin: auto;
  top: -5px;
  right: 0;
  bottom: 0;
  left: -5px;
  width: 25px;
  height: 25px;
  border-radius: 50%;
  border: 2px solid white;
  transition: all 0.5s;
}
`;

const Input = styled.input`
font-family: 'Inconsolata', monospace;
position: absolute;
margin: auto;
top: 0;
right: 0;
bottom: 0;
left: 0;
width: 50px;
height: 50px;
outline: none;
border: none;
background: crimson;
color: white;
text-shadow: 0 0 10px crimson;
padding: 0 80px 0 20px;
border-radius: 30px;
box-shadow: 0 0 25px 0 crimson, 0 20px 25px 0 rgba(0, 0, 0, 0.2);
transition: all 1s;
opacity: 0;
z-index: 5;
font-weight: bolder;
letter-spacing: 0.1em;

&:hover {
  cursor: pointer;
}

&:focus {
  width: 300px;
  opacity: 1;
  cursor: text;
}

&:focus ~ ${SearchButton} {
  right: -250px;
  background: #151515;
  z-index: 6;

  &::before {
    top: 0;
    left: 0;
    width: 25px;
  }

  &::after {
    top: 0;
    left: 0;
    width: 25px;
    height: 2px;
    border: none;
    background: white;
    border-radius: 0%;
    transform: rotate(-45deg);
  }
}

&::placeholder {
  color: white;
  opacity: 0.5;
  font-weight: bolder;
}
`;


const Navbar = () => {
  return (
    <Container>
      <Nav>
        <NavItems>
          <Title>Live Weather</Title>
          <SearchContainer>
            <SearchButton />
            <Input/>
          </SearchContainer>
        </NavItems>
      </Nav>
    </Container>
  );
};

export default Navbar;
