import React from "react";
import styled from "styled-components";

const Container = styled.div`
  background-color: black;
  height: 100vh;
`;

const CardContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-around;
  padding-top: 40px;

  &:before {
    width: 100px;
    height: 100px;
    content: "";
    position: absolute;
    background-color: rgb(144, 161, 255);
    z-index: -1;
    border-radius: 50%;
    left: 100px;
    top: 50px;
    transition: all 1s;
  }

  &:hover:before {
    transform: translate(-50px, 50px);
  }
`;

const Card = styled.div`
  position: relative;
  width: 320px;
  height: 350px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-between;
  padding: 20px 10px;
  border-radius: 10px;
  backdrop-filter: blur(30px);
  background-color: rgba(65, 65, 65, 0.308);
  border: 1px solid rgba(255, 255, 255, 0.089);
  cursor: pointer;
`;

const City = styled.p`
  font-weight: 700;
  font-size: 0.9em;
  letter-spacing: 1.2px;
  color: white;
`;

const Weather = styled.p`
  font-weight: 500;
  font-size: 0.7em;
  letter-spacing: 1.2px;
  color: rgb(197, 197, 197);
`;

const Temperature = styled.p`
  font-size: 1.8em;
  color: white;
`;

const MinMaxContainer = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const MinMax = styled.div`
  width: 50%;
  display: flex;
  flex-direction: column;
  align-items: ${(props) => (props.isMax ? "flex-start" : "flex-end")};
  justify-content: center;
  padding: 0px 20px;
  gap: 4px;
  border-left: ${(props) => (props.isMax ? "2px solid white" : "none")};
`;

const Heading = styled.p`
  font-size: 0.7em;
  font-weight: 600;
  color: white;
  `;

const Temp = styled.p`
  font-size: 0.6em;
  font-weight: 500;
  color: rgb(197, 197, 197);
`;

const Main = ({ weatherData }) => {
  console.log(weatherData);


  return (
    {weatherData}

  );
};

export default Main;
