import './App.css';
import { useState,useEffect } from 'react';
import axios from 'axios'
function App() {
  const [location,setLocation]=useState('')
  const [weatherData,setWeatherData]=useState(null)

  const API_KEY = "0306f3e1767baa02731f4d5bb9b8cff4";
  const API_URL = 'https://api.openweathermap.org/data/2.5/weather';

  const fetchWeatherData=async()=>{
    try{

      const response=await axios.get(API_URL,{params:{q:location,appid:API_KEY,units:"metric"}})
      console.log(response.data)
      setWeatherData(response.data)


    }catch(err){
      console.error("Error to Fetching Weather data",err)
    }

  }

  useEffect(()=>{
    if(location){
      fetchWeatherData()
    }
  },[location])


 const handlecheck=(e)=>{
  setLocation(e.target.value)
 }
  return (
    <div className="App">

       <input type='text' value={location} onChange={handlecheck}/>

       {weatherData && (
          <div className="main">
          <p className="header">{weatherData.name}</p>
          <div className="flex">
            <p className="day">{moment().format('dddd')}, <span>{moment().format('LL')}</span></p>
            <p className="description">{weatherData.weather[0].main}</p>
          </div>

          <div className="flex">
            <p className="temp">Temprature: {weatherData.main.temp} &deg;C</p>
            <p className="temp">Humidity: {weatherData.main.humidity} %</p>
          </div>

          <div className="flex">
            <p className="sunrise-sunset">Sunrise: {new Date(weatherData.sys.sunrise * 1000).toLocaleTimeString('en-IN')}</p>
            <p className="sunrise-sunset">Sunset: {new Date(weatherData.sys.sunset * 1000).toLocaleTimeString('en-IN')}</p>
          </div>

      </div>
       )}





    </div>
  );
}

export default App;
